import React, { useState, useEffect, useRef } from "react";
import { Html5QrcodeScanner } from "html5-qrcode";
import axios from "axios";
import "./App.css";

function App() {
  const [result, setResult] = useState(null);
  const [scanning, setScanning] = useState(false);
  const scannerRef = useRef(null);
  const readerRef = useRef(null); // Create a reference for the reader div

  useEffect(() => {
    if (scanning && readerRef.current) {
      const config = { fps: 10, qrbox: 250 };
      const verbose = false;

      // Create an instance of Html5QrcodeScanner and render it in the reader div
      const html5QrcodeScanner = new Html5QrcodeScanner("reader", config, verbose);
      html5QrcodeScanner.render(onScanSuccess, onScanFailure);
      scannerRef.current = html5QrcodeScanner;
    }
  }, [scanning]); // Re-run this effect when scanning state changes

  const startScanner = () => {
    setScanning(true);
  };

  const onScanSuccess = (decodedText, decodedResult) => {
    // Stop scanning after a successful scan
    if (scannerRef.current) {
      scannerRef.current.clear().catch((error) => {
        console.error("Failed to clear scanner. ", error);
      });
    }
    setScanning(false);
    checkUrlSafety(decodedText);
  };

  const onScanFailure = (error) => {
    console.warn(`QR Code scan error: ${error}`);
  };

  const checkUrlSafety = async (url) => {
    try {
      const response = await axios.post("http://localhost:5000/api/check-url", { url }, {
        headers: {
          "Content-Type": "application/json"
        }
      });
      setResult(response.data);
    } catch (error) {
      console.error("Error checking URL safety:", error);
      setResult({ safe: false, details: "Error occurred while checking URL." });
    }
  };


  return (
    <div className="App">
      <h1>QR Code Safety Checker</h1>

      {scanning ? (
        <>
          <div ref={readerRef} id="reader" style={{ width: "300px", margin: "auto" }}></div>
          <button onClick={() => setScanning(false)}>Stop Scanner</button>
        </>
      ) : (
        <button onClick={startScanner}>Start QR Code Scanner</button>
      )}

      {result && (
        <div className="result">
          <h2>Scan Result:</h2>
          <p>Status: {result.safe ? "Safe ✅" : "Potentially Dangerous ⚠️"}</p>
          <pre>{JSON.stringify(result.details, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
