import React, { useState, useEffect, useRef } from "react";
import { Html5QrcodeScanner } from "html5-qrcode";
import axios from "axios";
import "./App.css";

function App() {
  const [result, setResult] = useState(null);
  const [scanning, setScanning] = useState(false);
  const scannerRef = useRef(null);
  const readerRef = useRef(null);

  useEffect(() => {
    if (scanning && readerRef.current) {
      const config = { fps: 10, qrbox: 250 };
      const verbose = false;
      const html5QrcodeScanner = new Html5QrcodeScanner("reader", config, verbose);
      html5QrcodeScanner.render(onScanSuccess, onScanFailure);
      scannerRef.current = html5QrcodeScanner;
    }
  }, [scanning]);

  const startScanner = () => {
    setScanning(true);
  };

  const onScanSuccess = (decodedText) => {
    if (scannerRef.current) {
      scannerRef.current.clear().catch((error) => {
        console.error("Failed to clear scanner. ", error);
      });
    }
    setScanning(false);
    checkUrlSafety(decodedText);
  };

  const onScanFailure = (error) => {
    console.warn(`QR Code scan error: ${error}`);
  };

  const checkUrlSafety = async (url) => {
    try {
      const response = await axios.post("http://localhost:234/api/check-url", { url }, {
        headers: { "Content-Type": "application/json" }
      });

      console.log("API Response:", response.data); // Debugging log

      // Explicitly check if 'matches' array exists and has threats
      const isUnsafe = response.data && response.data.matches && response.data.matches.length > 0;

      const threatInfo = isUnsafe
        ? response.data.matches.map(match => ({
            type: match.threatType,
            url: match.threat.url,
          }))
        : [];

      setResult({ safe: !isUnsafe, url, threatInfo });

    } catch (error) {
      console.error("Error checking URL safety:", error);
      setResult({ safe: false, details: "Error occurred while checking URL." });
    }
  };


  return (
    <div className="App">
      <h1>QR Code Safety Checker</h1>

      {scanning ? (
        <>
          <div ref={readerRef} id="reader"></div>
          <button onClick={() => setScanning(false)}>Stop Scanner</button>
        </>
      ) : (
        <button onClick={startScanner}>Start QR Code Scanner</button>
      )}

      {result && (
        <div className={`result ${result.safe ? "safe" : "danger"}`}>
          <h2>Scan Result:</h2>

          {result.safe ? (
            <>
              <p>✅ Safe Link</p>
              <p>This link appears to be safe.</p>
              <a href={result.url} target="_blank" rel="noopener noreferrer">
                Open Scanned Link
              </a>
            </>
          ) : (
            <>
              <p>⚠️ Potentially Dangerous!</p>
              <p>This link might be harmful. Proceed with caution.</p>
              <pre>{JSON.stringify(result.details, null, 2)}</pre>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
