import React, { useState, useEffect, useRef } from "react";
import { Html5QrcodeScanner } from "html5-qrcode";
import axios from "axios";
import "./App.css";

function App() {
  const [result, setResult] = useState(null);
  const [scanning, setScanning] = useState(false);
  const scannerRef = useRef(null);
  const readerRef = useRef(null);

  useEffect(() => {
    if (scanning && readerRef.current) {
      const config = { fps: 10, qrbox: 250 };
      const verbose = false;
      const html5QrcodeScanner = new Html5QrcodeScanner("reader", config, verbose);
      html5QrcodeScanner.render(onScanSuccess, onScanFailure);
      scannerRef.current = html5QrcodeScanner;
    }
  }, [scanning]);

  const startScanner = () => {
    setScanning(true);
  };

  const onScanSuccess = (decodedText) => {
    if (scannerRef.current) {
      scannerRef.current.clear().catch((error) => {
        console.error("Failed to clear scanner. ", error);
      });
    }
    setScanning(false);
    checkUrlSafety(decodedText);
  };

  const onScanFailure = (error) => {
    console.warn(`QR Code scan error: ${error}`);
  };

  const checkUrlSafety = async (url) => {
    try {
      const response = await axios.post("http://localhost:234/api/check-url", { url }, {
        headers: { "Content-Type": "application/json" }
      });
      setResult({ ...response.data, url });
    } catch (error) {
      console.error("Error checking URL safety:", error);
      setResult({ safe: false, details: "Error occurred while checking URL." });
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800 px-4">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md">
        <h1 className="text-2xl font-bold text-center mb-4">QR Code Safety Checker</h1>

        {scanning ? (
          <>
            <div ref={readerRef} id="reader" className="w-full mb-4"></div>
            <button onClick={() => setScanning(false)} className="w-full bg-red-500 text-white py-2 rounded-lg shadow-md hover:bg-red-600">
              Stop Scanner
            </button>
          </>
        ) : (
          <button onClick={startScanner} className="w-full bg-blue-500 text-white py-2 rounded-lg shadow-md hover:bg-blue-600">
            Start QR Code Scanner
          </button>
        )}

        {result && (
          <div className="mt-6 p-4 rounded-lg shadow-md border">
            <h2 className="text-lg font-bold">Scan Result:</h2>

            {result.safe ? (
              <div className="text-green-600 mt-2">
                <p className="text-xl font-semibold">✅ Safe</p>
                <p className="text-gray-600">The link appears to be safe.</p>
                <a href={result.url} target="_blank" rel="noopener noreferrer" className="mt-3 block text-blue-500 underline">
                  Open Scanned Link
                </a>
              </div>
            ) : (
              <div className="text-red-600 mt-2">
                <p className="text-xl font-semibold">⚠️ Potentially Dangerous!</p>
                <p className="text-gray-600">This link may be harmful.</p>
                <div className="bg-red-100 p-3 rounded-lg text-xs mt-2">
                  <pre className="whitespace-pre-wrap">{JSON.stringify(result.details, null, 2)}</pre>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
